<!DOCTYPE html><html><head><title>innterface - mobile application design patterns library and user interface (UI) search engine</title><base href="<?php echo base_url(); ?>" /><meta charset="UTF-8" /><meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1" /><!-- to force IE8 to use latest standard --><meta name="viewport" content="width=device-width, initial-scale=1.0" /><meta name="description" content="innterface is a search platform which can give you inspiration and example of user interface (UI) design patterns for mobile application like iPhone and iPad." /><meta name="keywords" content="user interface,UI, mobile applications,app,apps,design patterns,screenshot" /><meta name="Copyright" content="Copyright © 2013 SayMac All rights reserved." /><link rel="shortcut icon" href="img/favicon.ico" /><!--[if lt IE 9]><script type="text/javascript" src="js/html5shiv.js"></script><![endif]--><link type="text/css" href="css/bootstrap.min.css" rel="stylesheet" media="screen"><style type="text/css">html, body {    width: 100%;    height: 100%;    margin: 0;    padding: 0;}#panel_left {    float: left;    width: 100%;    height: 100%;    margin: 0;    padding: 0;    overflow: hidden;}#panel_right {    float: right;    width: 0;    height: 100%;    margin: 0;    padding: 0;    overflow: hidden;}iframe.panel_iframe {    width: 100%;    height: 100%;    border: 0;    margin: 0;    padding: 0;}</style><script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.8.3/jquery.min.js"></script><script type="text/javascript" src="js/bootstrap.min.js"></script><script type="text/javascript">$(document).ready(function(){    is_split = false;    var url = '<?php echo site_url('synonym_mng/synonym_list'); ?>?word_id=<?php echo urlencode($word->id); ?>';    // 為了避免iframe會記住history的問題，這裡用刪除再建立iframe的方式，而不用更新iframe的src屬性的方式    add_left_iframe = function(url){        remove_left_iframe();        var $iframe = $('<iframe>').attr({id:'iframe_left',scrolling:'yes',src:url}).addClass('panel_iframe');        $iframe.appendTo('#panel_left');    };    add_right_iframe = function(url){        remove_right_iframe();        var $iframe = $('<iframe>').attr({id:'iframe_right',scrolling:'yes',src:url}).addClass('panel_iframe');        $iframe.appendTo('#panel_right');    };    remove_left_iframe = function(){        $('#iframe_left').remove();    };    remove_right_iframe = function(){        $('#iframe_right').remove();    };    open_split = function(url){        //if ( !is_split ) {            $('#panel_right').animate({                width: "50%"            }, 250, function(){                is_split = true;            });            $('#panel_left').animate({                width: "50%"            }, 250, function(){                is_split = true;            });        //}    };    close_split = function(){        //if ( is_split ) {            $('#panel_right').animate({                width: "0%"            }, 250, function(){                is_split = false;            });            $('#panel_left').animate({                width: "100%"            }, 250, function(){                is_split = false;            });        //}    };    fill_left_panel_url = function(url){        $('#iframe_left').attr('src', url);    };    fill_right_panel_url = function(url){        $('#iframe_right').attr('src', url);    };    add_left_iframe(url);    add_right_iframe('');});</script></head><body><div id="panel_left"></div><div id="panel_right"></div></body></html>